import React, { useReducer, setState } from 'react';
import './App.css';

function TransferList({transfers, approveTransfer}) {
  const [ignored, forceUpdate] = useReducer(x => x + 1, 0);
  function handleClick() {
    forceUpdate();
    console.log('force');
  }
  return (
    <div style={{ color: '#fff' }}>
      <h2>Transfers</h2>
      <table>
        <thead>
          <tr>
            <th>id</th>
            <th>amount</th>
            <th>to</th>
            <th>approvals</th>
            <th>sent</th>
          </tr>
        </thead>
        <tbody>
          {transfers.map((transfer) => (
            <tr key={transfer.id}>
              <td>{transfer.id}</td>
              <td>{transfer.amount}</td>
              <td>{transfer.to}</td>
              <td>
                {transfer.approvals}
                <button onClick={() => approveTransfer(transfer.id), handleClick()}>Approve</button>
              </td>
              <td>{transfer.sent ? 'yes' : 'no'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransferList;
